mapper.update:
UPDATE CHAT_ROOM
   SET FILE_NAME = #{file_name}
 WHERE CHAT_ROOM_NO = #{chat_room_no}

mapper.select:
SELECT COUNT(*)
  FROM CHAT_ROOM
 WHERE PRO_NO = #{pro_no} 
   AND BUYER_MEM_NO = #{buyer_mem_no}

SELECT CHAT_ROOM_NO
  FROM CHAT_ROOM
 WHERE PRO_NO = #{pro_no} 
   AND BUYER_MEM_NO = #{buyer_mem_no}

SELECT *
  FROM CHAT_ROOM
 WHERE PRO_NO = #{pro_no} 
   AND BUYER_MEM_NO = #{buyer_mem_no}
