mapper.select:
SELECT *
  FROM CHAT_ROOM
 WHERE PRO_NO = #{proNo} 
   AND BUYER_MEM_NO = #{buyerNo}

SELECT *
  FROM CHAT_ROOM
 WHERE SELLER_MEM_NO = #{memNo}
    OR BUYER_MEM_NO = #{memNo}

SELECT COUNT(*)
  FROM CHAT
 WHERE CHAT_ROOM_NO = #{chatRoomNo}
   AND CHAT_MEM_NO = #{memNo}
   AND CHAT_READ = 0

mapper.update:
UPDATE CHAT 
   SET CHAT_READ = 1
 WHERE CHAT_MEM_NO = #{chatMemNo}
   AND CHAT_ROOM_NO = #{chatRoomNo}

mapper.select:
SELECT MEM_ID
  FROM MEMBER
 WHERE MEM_NO = #{memNO}
