mapper.update:
UPDATE CHAT_ROOM
   SET FILE_NAME = #{fileName}
 WHERE CHAT_ROOM_NO = #{chatRoomNo}

mapper.select:
SELECT COUNT(*)
  FROM CHAT_ROOM
 WHERE PRO_NO = #{proNo} 
   AND BUYER_MEM_NO = #{buyerNo}

SELECT *
  FROM CHAT_ROOM
 WHERE PRO_NO = #{proNo} 
   AND BUYER_MEM_NO = #{buyerNo}

SELECT CHAT_ROOM_NO
  FROM CHAT_ROOM
 WHERE PRO_NO = #{proNo} 
   AND BUYER_MEM_NO = #{buyerNo}
