mapper.select:
    select * from member
	 where mem_name = #{mem_name}
       and mem_phone = #{mem_phone}

    select * from member
	 where mem_id = #{mem_id}
	   and mem_phone = #{mem_phone}
       and mem_email = #{mem_email}

mapper.update:
    update member
       set mem_pwd = #{mem_pwd}
	 where mem_id = #{mem_id}

mapper.select:
    select * from member
	 where mem_id = #{mem_id}
       and mem_pwd = #{mem_pwd}
